    fullResponse: [{"@context":"http://www.schema.org","@graph":[{"@type":"WebSite","@id":"https://12tomatoes.com/#website","url":"https://12tomatoes.com/","name":"12 Tomatoes","publisher":{"@id":"https://12tomatoes.com/#organization"},"potentialAction":{"@type":"SearchAction","target":"https://12tomatoes.com/?s={s}","query-input":{"@type":"PropertyValueSpecification","valueRequired":true,"valueMaxlength":100,"valueName":"s"}}},{"@type":"Organization","@id":"https://12tomatoes.com/#organization","name":"12 Tomatoes","description":"12 Tomatoes is a new cooking and recipe community. Each day we feature recipes that are healthy and cheap to make.","url":"https://12tomatoes.com/","logo":{"@type":"imageObject","@id":"https://12tomatoes.com/#logo","url":"https://12tomatoes.com/wp-content/themes/glp-theme/img/logo.png","width":365,"height":60,"caption":"12 Tomatoes"},"sameAs":["https://www.facebook.com/12Tomatoes","https://twitter.com/12tomatoes","https://pinterest.com/12tomatoes"]},{"@type":"WebPage","@id":"https://12tomatoes.com/magic-lemon-pudding/#webpage","url":"https://12tomatoes.com/magic-lemon-pudding/","inLanguage":"en-US","isPartOf":{"@id":"https://12tomatoes.com/#website"},"name":"Magic Lemon Pudding","primaryImageOfPage":{"@type":"imageObject","url":"https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17163509/Magic-Lemon-Pudding-OG-1-728x381.jpg","width":728,"height":381}},{"@type":"Article","@id":"https://12tomatoes.com/magic-lemon-pudding/#article","isPartOf":{"@id":"https://12tomatoes.com/magic-lemon-pudding/#webpage"},"mainEntityOfPage":"https://12tomatoes.com/magic-lemon-pudding/#webpage","publisher":{"@id":"https://12tomatoes.com/#organization"},"author":{"@id":"https://12tomatoes.com/#author"},"headline":"Magic Lemon Pudding","datePublished":"2020-06-17","dateModified":"2020-06-17","description":"This is a little bit of sunshine on a plate. ","image":{"@type":"imageObject","url":"https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17163509/Magic-Lemon-Pudding-OG-1-728x381.jpg","width":728,"height":381},"editor":{"@id":"https://12tomatoes.com/#editor"}},{"@type":"Person","@id":"https://12tomatoes.com/#author","name":"Rose Heichelbech","image":{"@type":"imageObject","@id":"https://12tomatoes.com/#personlogo","url":"https://cdn.greatlifepublishing.net/wp-content/author-images/roseheichelbech.jpg?ver=30ffb43fb836d3d6f5ef78def2f31957","caption":"Rose Heichelbech"}},{"@type":"Person","@id":"https://12tomatoes.com/#editor","name":"Rose Heichelbech","image":{"@type":"imageObject","@id":"https://12tomatoes.com/#personlogo","url":"https://cdn.greatlifepublishing.net/wp-content/author-images/roseheichelbech.jpg?ver=30ffb43fb836d3d6f5ef78def2f31957","caption":"Rose Heichelbech"}},{"@type":"Recipe","@id":"https://12tomatoes.com/magic-lemon-pudding/#recipe","inLanguage":"en-US","isPartOf":{"@id":"https://12tomatoes.com/magic-lemon-pudding/#webpage"},"author":{"@id":"https://12tomatoes.com/#author"},"name":"Magic Lemon Pudding","image":["https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17170731/Magic-Lemon-Pudding-Horizontal-9.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17170654/Magic-Lemon-Pudding-Vertical-17.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17170411/Magic-Lemon-Pudding-Vertical-18.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17170317/Magic-Lemon-Pudding-Horizontal-5.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17170234/Magic-Lemon-Pudding-Horizontal-3.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17170141/Magic-Lemon-Pudding-Horizontal-2.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17165956/Magic-Lemon-Pudding-Horizontal-4.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17165244/Magic-Lemon-Pudding-Vertical-15.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17165214/Magic-Lemon-Pudding-Horizontal-17.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17163636/Magic-Lemon-Pudding-Pinterest-1.jpg","https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/06/17163509/Magic-Lemon-Pudding-OG-1.jpg"],"prepTime":"P0DT0H25M0S","cookTime":"P0DT0H50M0S","recipeYield":"serves 4-6","keywords":"lemon, pudding, dessert, summer","recipeCategory":"dessert","recipeCuisine":"British","suitableForDiet":"20","recipeIngredient":["4 1/2 tablespoons butter, softened and divided","1/2 cup sugar","Juice of one lemon (about 2-3 tablespoons)","Zest of 1 lemon","2 eggs at room temperature, divided into yolks and whites","1/4 cup all-purpose flour","1 cup milk","1 teaspoon vanilla","1/4 teaspoon ground nutmeg"],"recipeInstructions":[{"@type":"HowToStep","text":"Preheat oven to 325˚. \r\n"},{"@type":"HowToStep","text":"Cream together sugar and 4 tablespoons butter. Add in egg yolks, flour, lemon zest, lemon juice, nutmeg, and vanilla and stir. Slowly add in milk, mixing the whole time. \r\n"},{"@type":"HowToStep","text":"In a separate bowl beat egg whites until stiff peaks form (about 5 minutes using an electric mixer). Fold the egg whites slowly into lemon batter so as to retain as much air as possible. Fold until mixture is all one color.  \r\n"},{"@type":"HowToStep","text":"Grease a 1-quart (8x8) baking dish or soufflé dish with remaining butter. Pour in batter.\r\n"},{"@type":"HowToStep","text":"Make a bain marie by placing the baking dish into a dry roasting pan and fill roasting pan with water until it reaches halfway up the side of the baking dish.\r\n"},{"@type":"HowToStep","text":"Bake for 40-50 minutes or until pudding feels springy when pressed. \r\n"},{"@type":"HowToStep","text":"Serve with berries, vanilla ice cream and/or a dusting of powdered sugar.\r\n"}]}]}],
